library(shiny)

# Define la interfaz de usuario
ui <- fluidPage(
  
  titlePanel("Calculadora de estimación de sexo"),
  
  p("A continuación, introduce las medidas requeridas en milímetros (mm):"),
  
  numericInput(
    inputId = "numericCALMA",
    label = "Longitud máxima del calcáneo (CALMA)",
    value = NULL
  ),
  
  numericInput(
    inputId = "numericCAAN",
    label = "Anchura media del calcáneo (CAAN)",
    value = NULL
  ),
  
  br(),
  strong("El resultado es:"),
  textOutput("sexResult"),
  
  br(),
  hr(),
  h5("Aviso de responsabilidad"),
  p("Esta calculadora tiene fines exclusivamente informativos y académicos. 
    Se basa en datos publicados y no sustituye el juicio profesional en contextos médicos, forenses o legales. 
    Los desarrolladores de esta herramienta no se hacen responsables por el uso, interpretación o consecuencias derivadas de los resultados obtenidos a través de esta calculadora.")
)

# Define el servidor
server <- function(input, output) {
  
  current_valueSEX <- reactive({
    if (is.null(input$numericCALMA) || is.null(input$numericCAAN)) {
      return(NA)
    }
    (0.175 * input$numericCALMA) + (0.065 * input$numericCAAN) - 14.990
  })
  
  output$sexResult <- renderText({
    score <- current_valueSEX()
    if (is.na(score)) {
      return("")
    } else if (score < -0.0220) {
      return("Femenino")
    } else {
      return("Masculino")
    }
  })
}

# Ejecuta la aplicación
shinyApp(ui = ui, server = server)
